// 3
var button_z3 = document.createElement("button");
button_z3.id = "button_z3";
button_z3.textContent = "Разблокировано"
var buttonz3 = true;
document.getElementById("zad3").appendChild(button_z3);
document.querySelector("#button_z3").onclick = function () {
    if (buttonz3 == true)
    {
        button_z3.textContent = "Заблокировано";
        input_z1.disabled = true;
        buttonz3 = false;
    }
    else
    {
        button_z3.textContent = "Разблокировано";
        input_z1.disabled = false;
        buttonz3 = true;
    }
}

// 4
