// 5
var button_z5 = document.createElement("button");
button_z5.id = "button_z5";
button_z5.textContent = "Скрыть поле";
var buttonz5 = true;
document.getElementById("zad5").appendChild(button_z5);
document.querySelector("#button_z5").onclick = function () {
    if (buttonz5 == true)
    {
        button_z5.textContent = "Показать поле";
        input_z1.style.display = "none";
        if (buttonz4 == true)
        {
            button_z1.disabled = true;
        }
        button_z3.disabled = true;
        button_z4.disabled = true;
        buttonz5 = false;
    }
    else
    {
        button_z5.textContent = "Скрыть поле";
        input_z1.style.display = "block";
        if (buttonz4 == true)
        {
            button_z1.disabled = false;
        }
        button_z3.disabled = false;
        button_z4.disabled = false;
        buttonz5 = true;
    }
}

// 6
function changeColor(element)
{
    if (element.style.backgroundColor === "green")
    {
        element.style.backgroundColor = "red";
    }
    else
    {
        element.style.backgroundColor = "green";
    }
}

// 7
function toggleColor(element) {
    if (element.classList.contains("green"))
    {
        element.classList.remove("green");
    }
    else
    {
        var shapes = document.querySelectorAll(".z7");
        for (var i = 0; i < shapes.length; i++)
        {
            shapes[i].classList.remove("green");
        }
        element.classList.add("green");
    }
}

