function autocompletefunc() {
    var input, filter, i, ul, li, suggestions;
    input = document.getElementById("myInput");
    filter = input.value.toUpperCase();
    suggestions = document.getElementById("mySuggestions");
    suggestions.innerHTML = "";
    if (filter.length > 0)
    {
        for (i = 0; i < search.length; i++)
        {
            if (search[i].toUpperCase().includes(filter))
            {
                var suggestion = document.createElement("div");
                suggestion.className = "autocomplete-suggestion";
                suggestion.textContent = search[i];
                suggestions.appendChild(suggestion);
                suggestion.onclick = function ()
                {
                    input.value = this.textContent;
                    suggestions.style.display = "none"
                };
            }
        }
        suggestions.style.display = "block";
    }
    else
    {
        suggestions.style.display = "none";
    }
}

// 9
document.addEventListener('DOMContentLoaded', () => {
    var mouseTrail = document.getElementById('mouse-trail');

    document.addEventListener('mousemove', (e) => {
        var mouseX = e.clientX - 10;
        var mouseY = e.clientY - 828;

        var trail = document.createElement('div');
        trail.classList.add('trail-dot');
        trail.style.left = mouseX + 'px';
        trail.style.top = mouseY + 'px';

        mouseTrail.appendChild(trail);

        setTimeout(() => {
            mouseTrail.removeChild(trail);
        }, 50);
    });
});

// 9.2
const dragElement = document.getElementById('drag-me');

let offsetX, offsetY;
let isDragging = false;

function dragStart(event) {
    isDragging = true;
    offsetX = event.clientX - dragElement.getBoundingClientRect().left;
    offsetY = event.clientY - dragElement.getBoundingClientRect().top;
}

function drag(event) {
    if (isDragging) {
        const x = event.clientX - offsetX;
        const y = event.clientY - offsetY;
        dragElement.style.left = x + 'px';
        dragElement.style.top = y + 'px';
    }
}

function dragEnd() {
    isDragging = false;
    dragElement.style.cursor = 'grab';
}

dragElement.addEventListener('mousedown', dragStart);
document.addEventListener('mousemove', drag);
document.addEventListener('mouseup', dragEnd);

